import React, { useState } from 'react';
import axios from 'axios';
import { motion } from 'framer-motion';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend } from 'recharts';
import { PredictionRequest, PredictionResponse, PredictionHistory } from './types';
import Background from './components/Background';

const App: React.FC = () => {
  const [features, setFeatures] = useState<number[]>([0, 0, 0]);
  const [prediction, setPrediction] = useState<number | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [history, setHistory] = useState<PredictionHistory[]>([]);

  const handleInputChange = (index: number, value: string) => {
    const newFeatures = [...features];
    newFeatures[index] = Number(value);
    setFeatures(newFeatures);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError(null);

    try {
      const response = await axios.post<PredictionResponse>(
        'http://127.0.0.1:5000/predict',
        { features } as PredictionRequest
      );

      setPrediction(response.data.prediction);
      setHistory([
        ...history,
        {
          features: [...features],
          prediction: response.data.prediction,
          timestamp: new Date()
        }
      ]);
    } catch (err) {
      setError('Failed to get prediction. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Background />
      <div className="min-h-screen py-6 flex flex-col justify-center sm:py-12">
        <div className="relative py-3 sm:max-w-xl sm:mx-auto">
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            className="relative px-4 py-10 bg-white/90 backdrop-blur-sm shadow-lg sm:rounded-3xl sm:p-20"
          >
            <div className="max-w-md mx-auto">
              <div className="divide-y divide-gray-200">
                <div className="py-8 text-base leading-6 space-y-4 text-gray-700 sm:text-lg sm:leading-7">
                  <h2 className="text-3xl font-bold text-primary mb-8">Inventory Prediction</h2>

                  <form onSubmit={handleSubmit} className="space-y-6">
                    <div className="space-y-4">
                      <div>
                        <label className="block text-sm font-medium text-gray-700">
                          Stock Available (kg/L)
                        </label>
                        <input
                          type="number"
                          value={features[0]}
                          onChange={(e) => handleInputChange(0, e.target.value)}
                          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700">
                          Reorder Level (kg/L)
                        </label>
                        <input
                          type="number"
                          value={features[1]}
                          onChange={(e) => handleInputChange(1, e.target.value)}
                          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        />
                      </div>

                      <div>
                        <label className="block text-sm font-medium text-gray-700">
                          Ingredient Code
                        </label>
                        <input
                          type="number"
                          value={features[2]}
                          onChange={(e) => handleInputChange(2, e.target.value)}
                          className="mt-1 block w-full rounded-md border-gray-300 shadow-sm focus:border-primary focus:ring focus:ring-primary focus:ring-opacity-50"
                        />
                      </div>
                    </div>

                    <button
                      type="submit"
                      disabled={loading}
                      className={`w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-primary hover:bg-primary-dark focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-primary ${
                        loading ? 'opacity-50 cursor-not-allowed' : ''
                      }`}
                    >
                      {loading ? (
                        <motion.svg
                          animate={{ rotate: 360 }}
                          transition={{ duration: 1, repeat: Infinity, ease: "linear" }}
                          className="w-5 h-5"
                          viewBox="0 0 24 24"
                        >
                          <circle
                            className="opacity-25"
                            cx="12"
                            cy="12"
                            r="10"
                            stroke="currentColor"
                            strokeWidth="4"
                          />
                          <path
                            className="opacity-75"
                            fill="currentColor"
                            d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
                          />
                        </motion.svg>
                      ) : (
                        'Predict'
                      )}
                    </button>
                  </form>

                  {error && (
                    <div className="mt-4 text-red-600 text-sm">{error}</div>
                  )}

                  {prediction !== null && (
                    <motion.div
                      initial={{ opacity: 0, y: 10 }}
                      animate={{ opacity: 1, y: 0 }}
                      className="mt-6 p-4 bg-white/50 backdrop-blur-sm rounded-lg"
                    >
                      <h3 className="text-lg font-medium text-gray-900">Predicted Purchase Quantity</h3>
                      <p className="mt-2 text-3xl font-bold text-secondary">
                        {prediction.toFixed(2)} kg/L
                      </p>
                    </motion.div>
                  )}

                  {history.length > 0 && (
                    <div className="mt-8">
                      <h3 className="text-lg font-medium text-gray-900 mb-4">Prediction History</h3>
                      <LineChart width={400} height={200} data={history.map(h => ({
                        timestamp: h.timestamp.toLocaleTimeString(),
                        prediction: h.prediction
                      }))}>
                        <CartesianGrid strokeDasharray="3 3" />
                        <XAxis dataKey="timestamp" />
                        <YAxis />
                        <Tooltip />
                        <Legend />
                        <Line type="monotone" dataKey="prediction" stroke="#3B82F6" />
                      </LineChart>
                    </div>
                  )}
                </div>
              </div>
            </div>
          </motion.div>
        </div>
      </div>
    </>
  );
};

export default App;
